INSERT INTO `exam-tb` VALUES (1,'КАКОЙ ЭЛЕКТРИЧЕСКИЙ ТОК ОПАСНЕЕ ДЛЯ ЧЕЛОВЕКА: ПОСТОЯННЫЙ ИЛИ ПЕРЕМЕННЫЙ',NULL,NULL,0,'3','4','Постоянный ток','Переменный ток','До 380 В опаснее переменный, а свыше 500 В постоянный ток опаснее переменного','','',NULL,'Электротравмой называются возникающие в организме изменения под действием электрического тока. Воздействие это нередко приводит к развитию терминального состояния, обусловленного фибрилляцией желудочков  сердца, остановкой дыхания за счет паралича дыхательного центра головного мозга или спазма дыхательных мышц. Электротравма вызывает морфно-функциональные изменения в центральной нервной, сердечно-сосудистой и дыхательной системах. Тяжесть и исход поражения при воздействии электрического тока зависят не только от физических параметров тока, но и от сопротивления кожи, ее состояния в момент контакта, времени воздействия и т.д. Переменный ток напряжением 380 и 220 В, имеющий промышленную частоту 50 Гц, более опасен чем постоянный ток того же напряжения. Однако при напряжении 500 В опасность постоянного и переменного токов уравнивается, а при напряжении свыше 500 В постоянный ток становится опаснее переменного.','3'),(2,'КАКОЕ ВОЗДЕЙСТВИЕ НА ОРГАНИЗМ ЧЕЛОВЕКА ОКАЗЫВАЕТ ЭЛЕКТРИЧЕСКИЙ ТОК',NULL,NULL,0,'3','4','Термическое действие','Механическое действие','Электролитическое действие','Биологическое действие','Электрический ток оказывает на человека все перечисленные воздействия',NULL,'Действие электрического тока на живую ткань носит разносторонний и своеобразный характер. Проходя через организм человека, электроток производит термическое, электролитическое, механическое и биологическое действия','5'),(3,'КАКИЕ ПЕТЛИ ЭЛЕКТРИЧЕСКОГО ТОКА (ПУТИ ПРОХОЖДЕНИЯ) ЧЕРЕЗ ТЕЛО ЧЕЛОВЕКА ЯВЛЯЮТСЯ НАИБОЛЕЕ ОПАСНЫМИ',NULL,NULL,0,'3','4','Обе руки - обе ноги, левая рука - ноги, рука - рука, голова - ноги','Правая рука - ноги, рука - голова, нога - нога, голова - правая нога','Правая рука - левая нога, голова - левая рука, нога - нога, голова - руки','Левая рука - правая нога, голова - правая рука, голова - руки, голова - левая нога','',NULL,'На исход поражения электрическим током влияет путь его прохождения через тело человека. Пути тока рука — рука, руки — ноги, руки — туловище являются наиболее опасными, так как в этих случаях возможно поражение сердца или легких наименее опасным является путь тока нога — нога','1'),(4,'ЧТО НЕОБХОДИМО СДЕЛАТЬ В ПЕРВУЮ ОЧЕРЕДЬ ПРИ ПОРАЖЕНИИ ЧЕЛОВЕКА ЭЛЕКТРИЧЕСКИМ ТОКОМ',NULL,NULL,0,'3','4','Позвонить в скорую помощь','Освободить пострадавшего от действия электрического тока, для этого необходимо произвести отключение той части установки, которой касается пострадавший','Оттащить пострадавшего за одежду не менее чем на 8 метров от места касания проводом земли или от оборудования, находящегося под напряжением','','',NULL,'В  случаях  поражения э  л  е  к  т  р  и  ч  е  с  к  и  м  т о к о м Обесточить пострадавшего.(Не забывай о собственной безопасности!) При отсутствии пульса на сонной артерии — нанести удар кулаком по грудине и приступить к реанимации. При коме — повернуть на живот. При электрических ожогах и ранах — наложить повязки. При переломах костей конечностей — шины. Вызвать  «Скорую  помощь». НЕДОПУСТИМО! - Прикасаться к пострадавшему без предварительного обесточивания. -  Прекращать реанимационные мероприятия до появления признаков биологической смерти.','2'),(5,'КАКУЮ ПЕРВУЮ ПОМОЩЬ НЕОБХОДИМО ОКАЗАТЬ ПОСТРАДАВШЕМУ ОТ ДЕЙСТВИЯ ЭЛЕКТРИЧЕСКОГО ТОКА В СЛУЧАЕ, ЕСЛИ ОН НАХОДИТСЯ В БЕССОЗНАТЕЛЬНОМ СОСТОЯНИИ, НО С СОХРАНИВШИМСЯ УСТОЙЧИВЫМ ДЫХАНИЕМ И ПУЛЬСОМ',NULL,NULL,0,'3','4','Повернуть на живот, очистить полость рта, убедиться в наличии пульса, наложить на раны повязки и шины','Ровно и удобно уложить, распустить и расстегнуть одежду, создать приток свежего воздуха, давать нюхать нашатырный спирт, обрызгивать водой, обеспечить полный покой и вызвать скорую помощь','Приложить холод к голове и вызвать скорую помощь','В полном объеме проводить реанимационные мероприятия','',NULL,'Если  нет сознания,  но есть пульс на сонной артерии убедиться  повернуть в наличии  на живот пульса  и очистить рот приложить  на раны  наложить холод наложить  шины к голове  повязки, вызвать скорую помощь','2'),(6,'В КАКОМ МАКСИМАЛЬНОМ РАДИУСЕ ОТ МЕСТА КАСАНИЯ ЗЕМЛИ ЭЛЕКТРИЧЕСКИМ ПРОВОДОМ МОЖНО ПОПАСТЬ ПОД \"ШАГОВОЕ\" НАПРЯЖЕНИЕ',NULL,NULL,0,'3','4','Непосредственно в месте касания земли','В радиусе 10 м от места касания','В радиусе 8 м от места касания','В радиусе 15 м от места касания','',NULL,'В  случаях  поражения «шагового»  напряжения в радиусе 8  метров  от места касания земли электрическим проводом можно попасть под  «шаговое» напряжение. передвигаться в зоне «шагового» напряжения следует в диэлектрических ботах или галошах либо «гусиным  шагом»: пятка шагающей ноги, не отрываясь от земли, приставляется к носку другой ноги. нельзя! приближаться  бегом к  лежащему  проводу.','3'),(7,'КАКИМ ОБРАЗОМ СЛЕДУЕТ ПЕРЕДВИГАТЬСЯ В ЗОНЕ \"ШАГОВОГО\" НАПРЯЖЕНИЯ',NULL,NULL,0,'3','4','Обычным шагом','\"Гусиным шагом\"','Широкими шагами в ускоренном темпе','Бегом','',NULL,'В  случаях  поражения «шагового»  напряжения в радиусе 8  метров  от места касания земли электрическим проводом можно попасть под  «шаговое» напряжение. передвигаться в зоне «шагового» напряжения следует в диэлектрических ботах или галошах либо «гусиным  шагом»: пятка шагающей ноги, не отрываясь от земли, приставляется к носку другой ноги. нельзя! приближаться  бегом к  лежащему  проводу.','2'),(8,'В КАКОМ СЛУЧАЕ ПРИ ПОРАЖЕНИИ ЭЛЕКТРИЧЕСКИМ ТОКОМ  ПОСТРАДАВШЕГО, ВЫЗОВ ВРАЧА ЯВЛЯЕТСЯ НЕОБЯЗАТЕЛЬНЫМ',NULL,NULL,0,'3','4','В случае, если пострадавший находится в сознании, но до этого был в состоянии обморока','В случае, если пострадавший получил сильнейшие ожоги','В случае, если пострадавший находится в бессознательном состоянии, но с сохранившимся устойчивым дыханием и пульсом','Во всех случаях поражения электрическим током вызов врача является обязательным независимо от состояния пострадавшего','',NULL,'В  случаях  поражения э  л  е  к  т  р  и  ч  е  с  к  и  м  т о к о м Обесточить  пострадавшего.(Не забывай о собственной безопасности!) При отсутствии пульса на сонной артерии — нанести удар кулаком по грудине и приступить к реанимации. При коме — повернуть на живот. При электрических ожогах и ранах — наложить повязки. При переломах костей конечностей — шины. Вызвать  «Скорую  помощь». НЕДОПУСТИМО! - Прикасаться к пострадавшему без предварительного обесточивания.  -  Прекращать реанимационные мероприятия до появления признаков биологической смерти.','4'),(9,'КАКУЮ ПЕРВУЮ ПОМОЩЬ НЕОБХОДИМО ОКАЗАТЬ ЧЕЛОВЕКУ, ПОПАВШЕМУ ПОД РАЗРЯД МОЛНИИ',NULL,NULL,0,'3','4','Закопать пострадавшего в землю','Провести реанимационные мероприятия, дать обезболивающие и противошоковые средства','Дать обезболивающее средство, обеспечить покой в положении полусидя','','',NULL,'Пострадавший от удара молнией нуждается в незамедлительной медицинской помощи, поэтому нужно вызвать скорую. Тело пораженного молнией не находится под напряжением, в отличие от других электротравм. Поэтому к человеку можно смело прикасаться, резиновые перчатки и другая защита не нужны. Если пострадавший не дышит, немедленно приступают к искусственному дыханию, уложив человека на спину на твердую поверхность. Оценивают сердечную деятельность, прощупав пульс на сонной артерии или приложив ухо к грудной клетке. Если сердцебиение не выслушивается, начинают непрямой массаж сердца. ','2'),(10,'ВЫБЕРИТЕ ПРАВИЛЬНЫЙ ПОРЯДОК ДЕЙСТВИЙ ПО СПАСЕНИЮ ЖИЗНИ И СОХРАНЕНИЮ ЗДОРОВЬЯ ПОСТРАДАВШЕГО',NULL,NULL,0,'3','4','Вызвать скорую помощь, освободить пострадавшего от воздействия на него опасного производственного фактора, выполнить необходимые мероприятия по спасению пострадавшего ','Вызвать скорую помощь, оценить состояние пострадавшего, освободить пострадавшего от воздействия на него опасного производственного фактора, выполнить необходимые мероприятия по спасению пострадавшего','Освободить пострадавшего от воздействия на него опасного производственного фактора, оценить состояние пострадавшего, вызвать скорую помощь, выполнить необходимые мероприятия по спасению пострадавшего','Оценить состояние пострадавшего, освободить пострадавшего от воздействия на него опасного производственного фактора, выполнить необходимые мероприятия по спасению пострадавшего, вызвать скорую помощь','',NULL,'Порядок выполнения действий по спасению жизни и сохранению здоровья пострадавшего должен быть следующим: 1. Освободить пострадавшего от воздействия на него опасного или вредного производственного фактора (электрического тока, химических веществ, воды, механического воздействия и др.) с использованием штатных или подручных средств и безопасных для себя приемов. 2. Оценить состояние пострадавшего, определить характер и степень повреждения.  3. Вызвать медицинских работников, готовить пострадавшего к транспортировке в лечебное учреждение.  4. Придать пострадавшему безопасное положение, осторожно обнажить поврежденные участки, части тела и принять решение о мерах неотложной помощи.  5. Выполнить необходимые мероприятия по спасению пострадавшего в порядке срочности - восстановить дыхание, остановить кровотечение, иммобилизовать место перелома, наложить повязки и т.п.  6. Не оставлять пострадавшего без внимания, поддерживать основные жизненные функции его организма до прибытия медицинских работников.','3'),(11,'УКАЖИТЕ ПОСЛЕДОВАТЕЛЬНОСТЬ ДЕЙСТВИЙ ПРИ ОКАЗАНИИ ПЕРВОЙ ПОМОЩИ ПОСТРАДАВШЕМУ ПРИ ПОТЕРЕ СОЗНАНИЯ И ОТСУТСТВИИ ПУЛЬСА НА СОННОЙ АРТЕРИИ',NULL,NULL,0,'3','4','Убедиться в отсутствии пульса на сонной артерии, убедиться в отсутствии признаков дыхания, освободить грудную клетку от одежды и расстегнуть поясной ремень, прикрыть двумя пальцами мечевидный отросток, нанести удар кулаком по грудине, проверить пульс, при отсутствии пульса перейти к непрямому массажу сердца ','Убедиться в отсутствии пульса на сонной артерии, освободить грудную клетку от одежды и расстегнуть поясной ремень, прикрыть двумя пальцами мечевидный отросток, нанести удар по грудине, перейти к непрямому массажу сердца, сделать вдох искусственного дыхания','Убедиться в отсутствии пульса на сонной артерии, освободить грудную клетку от одежды и расстегнуть поясной ремень, прикрыть двумя пальцами мечевидный отросток, нанести удар кулаком по грудине, проверить пульс, при отсутствии пульса перейти к непрямому массажу сердца ','Убедиться в отсутствии пульса на сонной артерии, убедиться в отсутствии признаков дыхания, освободить грудную клетку от одежды и расстегнуть поясной ремень, прикрыть двумя пальцами мечевидный отросток, нанести удар кулаком по грудине, перейти к непрямому массажу сердца','',NULL,'Последовательность действий при оказании первой медицинской помощи 5. Правила определения признаков клинической смерти Правило первое. Чтобы сделать вывод о наступлении клинической смерти у неподвижно лежащего пострадавшего, достаточно убедиться в отсутствии сознания и пульса на сонной артерии. Внимание! В случае клинической смерти, особенно после поражения электрическим током, первое с чего необходимо начать помощь, - нанести удар по грудине пострадавшего. Если удар нанесен в течение первой минуты после остановки сердца, то вероятность оживления превышает 50%.   Если после нескольких ударов не появился пульс на сонной артерии? Приступить к непрямому массажу сердца.','3'),(12,'КАКИМ ОБРАЗОМ НЕОБХОДИМО ОБРАБАТЫВАТЬ ОЖОГ С НАРУШЕНИЕМ ЦЕЛОСТНОСТИ ОЖОГОВЫХ ПУЗЫРЕЙ И КОЖИ',NULL,NULL,0,'3','4','Накрыть сухой чистой тканью, забинтовать обожженную поверхность','Накрыть сухой чистой тканью, поверх ткани приложить холод на 20-30 минут','Промыть обожженную поверхность водой и забинтовать','Промыть обожженную поверхность водой, забинтовать и приложить холод','',NULL,'Инструкция п.12.1. Правила оказания помощи в случаях термических ожогов с повреждением целостности кожи и ожоговых пузырей Правило первое. Накрыть обожженную поверхность сухой чистой тканью. Правило второе. Поверх сухой ткани на 20-30 минут приложить холод. Правило третье. Предложить обильное теплое питье и при отсутствии аллергических реакций 2-3 таблетки анальгина. Правило четвертое. Нельзя смазывать ожог йодом, зеленкой, лосьонами, мазями. Правило пятое. Нельзя предлагать пострадавшему газированную воду. Правило шестое. Нельзя промывать место ожога водой или прикладывать на поврежденную кожу снег или холод.','2'),(13,'ЕСЛИ ПОРАЖЕНИЕ ЭЛЕКТРИЧЕСКИМ ТОКОМ ПРОИЗОШЛО НА ВЫСОТЕ, ГДЕ НЕОБХОДИМО НАЧИНАТЬ ОКАЗЫВАТЬ ПЕРВУЮ ПОМОЩЬ, НА ЗЕМЛЕ ИЛИ НА ВЫСОТЕ',NULL,NULL,0,'3','4','Помощь нужно начинать оказывать там, где все произошло, чтобы не упустить время.','Место оказания первой помощи не имеет значения.','Пострадавшего   нужно   как   можно   быстрее  спустить  с   высоты,   чтобы приступить к оказанию помощи в более удобных и безопасных условиях.','','',NULL,'Если пострадавший попал под напряжение электрического тока на высоте, необходимо сначала обесточить место происшествия (см. п.1.2.) и только затем приступить к экстренному спуску. НЕЛЬЗЯ! Приступать к любым действиям без предварительного обесточивания места происшествия. НЕЛЬЗЯ! Терять время на оказание помощи на высоте. ','3'),(14,'КАКУЮ ПЕРВУЮ ПОМОЩЬ НЕОБХОДИМО ОКАЗАТЬ ПОСТРАДАВШЕМУ ОТ ДЕЙСТВИЯ ЭЛЕКТРИЧЕСКОГО ТОКА В СЛУЧАЕ, ЕСЛИ ОН НАХОДИТСЯ В БЕССОЗНАТЕЛЬНОМ СОСТОЯНИИ, НО С СОХРАНИВШИМСЯ УСТОЙЧИВЫМ ДЫХАНИЕМ И ПУЛЬСОМ',NULL,NULL,0,'3','4','Повернуть на живот, очистить полость рта, убедиться в наличии пульса, наложить на раны повязки и шины','Ровно и удобно уложить, распустить и расстегнуть одежду, создать приток свежего воздуха, давать нюхать нашатырный спирт, обрызгивать водой, обеспечить полный покой и вызвать скорую помощь','Приложить холод к голове и вызвать скорую помощь','В полном объеме проводить реанимационные мероприятия','',NULL,'Правила оказания помощи в случае кратковременной потери сознания (обморока) - Правило первое. Убедиться в наличии пульса на сонной артерии. Правило второе. Расстегнуть воротник одежды, поясной ремень и приподнять ноги. Следует как